import {useState} from "react";
import Card from "../UI/Card";
import classes from './AddUser.module.css'
import Button from "../UI/Button";
import ErrorModal from "../UI/ErrorModal";
import Wrapper from "../Helpers/Wrapper";


const AddUser = (props) => {
    const [enteredUsername, setEnteredUsername] = useState('');
    const [enteredAge, setEnteredAge] = useState('');


    const [error, setError] = useState();


    const usernameInputChangeHandler = (event) => {
        setEnteredUsername(event.target.value);
    }

    const ageInputChangeHandler = (event) => {
        setEnteredAge(event.target.value);
    }

    const addUserHandler = (event) => {
        event.preventDefault();
        if (enteredUsername.trim().length === 0 || enteredAge.trim().length === 0) {
            setError({
                title: 'Invalid input',
                message: 'Please enter a valid name and age (non-empty values)'
            });
            return;
        }
        if (+enteredAge < 1) {
            setError({
                title: 'Invalid age',
                message: 'Please enter a valid age (>0)'
            });
            return;
        }
        const enteredUserData = [enteredUsername, enteredAge];
        props.onAddUserData(enteredUserData);
        setEnteredUsername('');
        setEnteredAge('')
    }

    const errorHandler = () => {
        setError(null);
    }

    return <Wrapper>
        {error && <ErrorModal onClose={errorHandler} title={error.title} message={error.message}/>}
        <Card className={classes.input}>
            <form onSubmit={addUserHandler}>
                <label htmlFor="username" >Username</label>
                <input value={enteredUsername} onChange={usernameInputChangeHandler} id="username" type="text"/>
                <label htmlFor="age" >Age (Years)</label>
                <input value={enteredAge} onChange={ageInputChangeHandler} id="age" type="number"/>
                <Button className="" type="submit">Add User</Button>
                {/*<button type="submit">Add User</button>*/}
            </form>
        </Card>
    </Wrapper>

};
export default AddUser;
