import {useState} from "react";
import Card from "../UI/Card";
import classes from './AddUser.module.css'
import Button from "../UI/Button";


const AddUser = (props) => {
    const [enteredUsername, setEnteredUsername] = useState('');
    const [enteredAge, setEnteredAge] = useState('');
    const [usernameIsValid, setUsernameIsValid] = useState(true);
    const [ageIsValid, setsAgeIsValid] = useState(true);


    const usernameInputChangeHandler = (event) => {
        if (event.target.value.trim().length > 0) {
            setUsernameIsValid(true);
        }
        setEnteredUsername(event.target.value);
    }

    const ageInputChangeHandler = (event) => {
        if (event.target.value.trim().length > 0  && event.target.value > -1) {
            setsAgeIsValid(true);
        }
        setEnteredAge(event.target.value);
    }

    const addUserHandler = (event) => {
        event.preventDefault();
        if (enteredUsername.trim().length === 0) {
            setUsernameIsValid(false);
            alert('Please enter a valid name and age (non-empty values)');
            return;
        }
        if (enteredAge.trim().length === 0 || enteredAge < 0) {
            setsAgeIsValid(false);
            alert('Please enter a valid name and age (non-empty values)');
            return;
        }
        if (enteredAge < 0)
        {
            setsAgeIsValid(false);
        }
        console.log('usernameValid? ' + usernameIsValid, 'ageValid? ' + ageIsValid)
        const enteredUserData = [enteredUsername, enteredAge];
        props.onAddUserData(enteredUserData);
        setEnteredUsername('');
        setEnteredAge('')
    }

    return<Card className={classes.input}>
        <form onSubmit={addUserHandler}>
            <label htmlFor="username" >Username</label>
            <input value={enteredUsername} onChange={usernameInputChangeHandler} id="username" type="text"/>
            <label htmlFor="age" >Age (Years)</label>
            <input value={enteredAge} onChange={ageInputChangeHandler} id="age" type="number"/>
            <Button className="" type="submit">Add User</Button>
            {/*<button type="submit">Add User</button>*/}
        </form>
    </Card>

};
export default AddUser;
