import React from 'react';
import AddUser from "./components/Users/AddUser";


function App() {
    const [users, setUsers] = useState([
        { username: '', age: '' , id: 'u1'}
    ]);

    const addUsername = enteredUsername => {
        setUsers(prevUsers => {
            const updatedUsers = [...prevUsers];
            updatedUsers.unshift({ username: enteredUsername, id: Math.random().toString() });
            return updatedUsers;
        });
    };

    const addAge = enteredAge => {
        setUsers(prevUsers => {
            const updatedUsers = [...prevUsers];
            updatedUsers.unshift({ age: enteredAge });
            return updatedUsers;
        });
    };

  return (
    <div>
      <AddUser onAddUsername={addUsername} onAddAge={addAge}/>
    </div>
  );
}

export default App;
