import {useState} from "react";


const AddUser = (props) => {
    const [enteredUsername, setEnteredUsername] = useState('');
    const [enteredAge, setEnteredAge] = useState('');
    const [userIsValid, setUserIsValid] = useState(true);
    const [ageIsValid, setsAgeIsValid] = useState(true);


    const userInputChangeHandler = (event) => {
        if (event.target.value.trim().length > 0) {
            setUserIsValid(true);
        }
        setEnteredUsername(event.target.value);
    }

    const ageInputChangeHandler = (event) => {
        if (event.target.value.trim().length > 0) {
            setsAgeIsValid(true);
        }
        setEnteredAge(event.target.value);
    }

    const addUserHandler = (event) => {
        event.preventDefault();
        if (enteredUsername.trim().length === 0) {
            setUserIsValid(false);
            return;
        }
        if (enteredAge.trim().length === 0) {
            setsAgeIsValid(false);
            return;
        }
        props.onAddUser(enteredUsername);
        props.onAddAge(enteredUsername);
    }

    return <form onSubmit={addUserHandler}>
        <label htmlFor="username" >Username</label>
        <input onChange={userInputChangeHandler} id="username" type="text"/>
        <label htmlFor="age" >Age (Years)</label>
        <input onChange={ageInputChangeHandler} id="age" type="number"/>
        <button type="submit">Add Suer</button>
    </form>
};
export default AddUser;
